class a{constructor(t){this.app=t,this.instance=document.querySelector("nav"),this.top=this.instance.querySelector(".nav_top"),this.items=this.instance.querySelectorAll(".nav_item"),this.navDots=this.instance.querySelector(".nav_dots"),this.navLogo=this.instance.querySelector(".nav_logo"),this.items.forEach((e,s)=>{const i=.05*s+.1;e.style.setProperty("--delay",`${i}s`)}),this.setupEventListeners(),this.resize(),this.app.on("resize",()=>this.resize())}setupEventListeners(){this.top.addEventListener("click",t=>{window.innerWidth>=991||this.navLogo&&this.navLogo.contains(t.target)||this.toggleMenu()}),this.navDots.addEventListener("click",t=>{t.stopPropagation(),this.toggleMenu()}),this.setupClickOutside()}toggleMenu(){this.instance.classList.contains("active")?this.instance.classList.remove("active"):this.instance.classList.add("active")}setupClickOutside(){document.addEventListener("click",t=>{!this.instance.classList.contains("active")||window.innerWidth>=991||this.instance.contains(t.target)||this.instance.classList.remove("active")})}resize(){const t=this.top.offsetHeight;this.instance.style.setProperty("--topHeight",`${t}px`)}}export{a as default};
